import React, { useState, useEffect } from 'react';
import { API_BASE_URL } from '../constant';

export default function AddAirplane() {
  const [tailNum, setTailNum] = useState('');
  const [model, setModel] = useState('');
  const [seatCapacity, setSeatCapacity] = useState('');
  const [manufacturingYear, setManufacturingYear] = useState('');
  const [maintenanced, setMaintenanced] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Set the document title
    document.title = 'Add Airplane | Airline Management System';
  }, []);

  const handleAddAirplane = async () => {
    setLoading(true);

    if (!tailNum || !model) {
      alert('Tail Number and Aircraft Model are required.');
      setLoading(false);
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/procedures/add_airplane`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          tail_num: tailNum,
          model: model,
          seat_capacity: parseInt(seatCapacity) || 0,
          manufacturingYear: manufacturingYear,
          maintenanced: maintenanced,
          // Including other required fields with default values
          airlineID: "DEFAULT",
          speed: 0,
          plane_type: model,
          locationID: "",
          neo: false,
        }),
      });

      if (response.ok) {
        // Reset form after successful submission
        setTailNum('');
        setModel('');
        setSeatCapacity('');
        setManufacturingYear('');
        setMaintenanced(false);

        // Navigate to dashboard or show success message
        alert('Airplane added successfully!');
      } else {
        const errorData = await response.json();
        alert(errorData.error || 'Failed to add airplane.');
      }
    } catch (err) {
      console.error('Error adding airplane:', err);
      alert('An unexpected error occurred.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gray-50 min-h-screen p-4">
      <div className="max-w-3xl mx-auto">
        <a href="/dashboard" className="inline-flex items-center text-gray-600 mb-6">
          <svg className="w-5 h-5 mr-1" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M19 12H5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M12 19L5 12L12 5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          Back to Dashboard
        </a>

        <h1 className="text-3xl font-bold mb-2 flex items-center">
          <svg className="w-8 h-8 mr-3" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M3 8H21L21 10H3V8Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M5 10H19V17H5V10Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M8 17V19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M16 17V19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M12 10L15 3" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          Add Airplane
        </h1>
        <p className="text-gray-500 mb-8">Register a new airplane in the system</p>

        <div className="bg-white p-8 rounded-lg shadow-sm">
          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Tail Number</label>
              <input
                type="text"
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={tailNum}
                onChange={(e) => setTailNum(e.target.value)}
                placeholder="e.g. N12345"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Aircraft Model</label>
              <input
                type="text"
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={model}
                onChange={(e) => setModel(e.target.value)}
                placeholder="e.g. Boeing 737-800"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Passenger Capacity</label>
              <input
                type="number"
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={seatCapacity}
                onChange={(e) => setSeatCapacity(e.target.value)}
                placeholder="e.g. 189"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Manufacturing Year</label>
              <input
                type="text"
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={manufacturingYear}
                onChange={(e) => setManufacturingYear(e.target.value)}
                placeholder="e.g. 2015"
              />
            </div>

            <div className="flex items-center">
              <input
                id="maintenance"
                type="checkbox"
                className="h-4 w-4 text-blue-600 rounded"
                checked={maintenanced}
                onChange={(e) => setMaintenanced(e.target.checked)}
              />
              <label htmlFor="maintenance" className="ml-2 text-sm text-gray-700">
                Aircraft is currently in maintenance
              </label>
            </div>
          </div>

          <div className="mt-8 flex justify-end">
            <button 
              onClick={handleAddAirplane} 
              disabled={loading}
              className="flex items-center justify-center bg-blue-600 hover:bg-blue-700 text-white py-2 px-6 rounded-md font-medium focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <svg className="w-5 h-5 mr-2" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 5V19" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M5 12H19" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
              Add Airplane
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
